import { variant as SsVariant } from "styled-system";
export const variant = ({
  prop = "variant",
  scale,
  variants = {
    primary: {}
  }
}) => SsVariant({
  prop,
  scale: scale ? `variants.${scale}` : undefined,
  variants
});
export const IntentVariants = variant({
  scale: "intents"
});
export const TypographyVariants = variant({
  scale: "typography"
});